import React, { useState } from "react";
import { motion } from "framer-motion";
import {
    Phone,
    Mail,
    MapPin,
    Clock,
    Calendar,
    Send,
    CheckCircle,
    User,
    Heart,
    AlertCircle,
    FileText,
    Shield,
    Cigarette,
    Wine
} from "lucide-react";

/**
 * Page Admission – version améliorée
 * 1. Section héros
 * 2. Blocs infos contact
 * 3. Formulaire d'admission très détaillé + esthétique améliorée (boutons / radio custom)
 */

const Admission = () => {
    /*─────────────── ÉTAT ───────────────*/
    const [formData, setFormData] = useState({
        /* Demandeur */
        requesterName: "",
        requesterRelation: "",
        requesterEmail: "",
        requesterPhone: "",

        /* Résident */
        residentFirstName: "",
        residentLastName: "",
        residentDOB: "",
        cognitiveLevel: "",
        medications: "",
        allergies: "",
        medicalConditions: "",
        mobilityNeeds: "",

        /* Détails */
        dietaryRestrictions: "",
        interests: "",
        specialRequests: "",

        /* Visite & Occupation */
        visitDate: "",
        occupancyDelay: "", // nouvel attribut

        /* Dépendances */
        smoker: "no",
        alcohol: "no",
        otherDependency: "no",
        otherDetails: "",

        /* Documents */
        hasHealthCard: false,
        hasMedicalReports: false,
        hasInsurance: false
    });
    const [isSubmitted, setIsSubmitted] = useState(false);

    /*────────────────── HANDLERS ─────────────────*/
    const handleChange = (e) => {
        const { name, type, value, checked } = e.target;
        setFormData((prev) => ({
            ...prev,
            [name]: type === "checkbox" ? checked : value
        }));
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        setIsSubmitted(true);
        // ici tu brancheras ton backend / email service
        setTimeout(() => setIsSubmitted(false), 6000);
    };

    /*────────────────── DATA ─────────────────*/
    const contactInfo = [
        {
            icon: Phone,
            title: "Téléphone",
            details: "(450) 555-0123",
            description: "Disponible 24h/7 pour urgences"
        },
        {
            icon: Mail,
            title: "Email",
            details: "info@residencegranby.ca",
            description: "Réponse sous 24h"
        },
        {
            icon: MapPin,
            title: "Adresse",
            details: "123 Rue Principale, Granby, QC J2G 1A1",
            description: "Stationnement gratuit"
        },
        {
            icon: Clock,
            title: "Heures de visite",
            details: "Lundi au dimanche : 9h à 18h",
            description: "Visites sur rendez-vous"
        }
    ];

    const cognitiveOptions = [
        { value: "autonome", label: "Autonome" },
        { value: "leger", label: "Léger" },
        { value: "modere", label: "Modéré" },
        { value: "avance", label: "Avancé" }
    ];

    const occupancyOptions = [
        { value: "0-2w", label: "Immédiat – 2 semaines" },
        { value: "2-4w", label: "2 – 4 semaines" },
        { value: "1-2m", label: "1 – 2 mois" },
        { value: "2m+", label: "> 2 mois" }
    ];

    /*─────────────────────── RENDER ───────────────────*/
    return (
        <div className="font-merriweather">
            {/*────────── Hero ──────────*/}
            <section className="relative min-h-[60vh] flex items-center justify-center">
                <div
                    className="absolute inset-0 bg-cover bg-center bg-no-repeat"
                    style={{
                        backgroundImage:
                            "linear-gradient(135deg, rgba(166,204,230,.9), rgba(186,174,217,.9)), url('https://images.pexels.com/photos/8728559/pexels-photo-8728559.jpeg')"
                    }}
                />
                <div className="relative z-10 text-white text-center space-y-6 px-4">
                    <motion.h1
                        initial={{ opacity: 0, y: 30 }}
                        animate={{ opacity: 1, y: 0 }}
                        transition={{ duration: 0.8 }}
                        className="text-5xl font-playfair font-bold"
                    >
                        Contact & Admission
                    </motion.h1>
                    <motion.p
                        initial={{ opacity: 0, y: 30 }}
                        animate={{ opacity: 1, y: 0 }}
                        transition={{ delay: 0.3, duration: 0.8 }}
                        className="text-xl text-white/90 max-w-3xl mx-auto"
                    >
                        Merci de nous considérer comme milieu de vie pour votre proche. Remplissez ce formulaire détaillé et un membre de l’équipe vous contactera dans les 48 heures.
                    </motion.p>
                </div>
            </section>

            {/*────────── Infos contact ──────────*/}
            <section className="py-20 bg-white">
                <div className="max-w-7xl mx-auto px-4 grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-8">
                    {contactInfo.map(({ icon: Icon, title, details, description }, idx) => (
                        <motion.div
                            key={title}
                            initial={{ opacity: 0, y: 30 }}
                            whileInView={{ opacity: 1, y: 0 }}
                            transition={{ duration: 0.6, delay: idx * 0.1 }}
                            className="text-center"
                        >
                            <div className="w-16 h-16 bg-[#A6CCE6]/20 rounded-xl flex items-center justify-center mx-auto mb-4">
                                <Icon size={32} className="text-[#4D4D4D]" />
                            </div>
                            <h3 className="text-xl font-bold text-gray-900 mb-2">{title}</h3>
                            <p className="text-gray-700 font-medium mb-1">{details}</p>
                            <p className="text-gray-500 text-sm">{description}</p>
                        </motion.div>
                    ))}
                </div>
            </section>

            {/*────────── Formulaire ──────────*/}
            <section className="py-20 bg-gradient-to-b from-[#A6CCE6]/20 to-white">
                <div className="max-w-6xl mx-auto bg-white rounded-2xl shadow-granby-lg p-10">
                    <motion.h2
                        initial={{ opacity: 0, y: 30 }}
                        whileInView={{ opacity: 1, y: 0 }}
                        transition={{ duration: 0.8 }}
                        className="text-4xl font-playfair font-bold text-center text-gray-900 mb-10"
                    >
                        Demande d'admission détaillée
                    </motion.h2>

                    {isSubmitted ? (
                        <div className="text-center py-10">
                            <CheckCircle className="text-green-500 mx-auto mb-6" size={64} />
                            <h3 className="text-2xl font-bold text-gray-900 mb-4">Demande envoyée !</h3>
                            <p className="text-gray-600 max-w-lg mx-auto">
                                Merci de nous considérer comme milieu de vie pour votre proche. Un membre de l’équipe vous contactera dans les prochaines 48 heures.
                            </p>
                        </div>
                    ) : (
                        <form onSubmit={handleSubmit} className="space-y-12">
                            {/* Demandeur */}
                            <SectionTitle icon={User} color="#A6CCE6" title="Informations du demandeur" />
                            <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                                {inputField({ label: "Nom complet *", name: "requesterName", value: formData.requesterName, handleChange })}
                                {inputField({ label: "Lien avec le résident *", name: "requesterRelation", value: formData.requesterRelation, handleChange, placeholder: "Fille, fils, tuteur légal…" })}
                                {inputField({ label: "Email *", name: "requesterEmail", type: "email", value: formData.requesterEmail, handleChange })}
                                {inputField({ label: "Téléphone *", name: "requesterPhone", type: "tel", value: formData.requesterPhone, handleChange })}
                            </div>

                            {/* Résident */}
                            <SectionTitle icon={Heart} color="#BAAED9" title="Résident concerné" />
                            <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                                {inputField({ label: "Prénom *", name: "residentFirstName", value: formData.residentFirstName, handleChange })}
                                {inputField({ label: "Nom de famille *", name: "residentLastName", value: formData.residentLastName, handleChange })}
                                {inputField({ label: "Date de naissance *", name: "residentDOB", type: "date", value: formData.residentDOB, handleChange })}
                                {/* Niveau cognitif */}
                                <div>
                                    <label className="block text-sm font-semibold text-gray-700 mb-2">Niveau cognitif *</label>
                                    <div className="grid grid-cols-2 gap-2">
                                        {cognitiveOptions.map((opt) => (
                                            <RadioPill
                                                key={opt.value}
                                                name="cognitiveLevel"
                                                value={opt.value}
                                                label={opt.label}
                                                checked={formData.cognitiveLevel === opt.value}
                                                onChange={handleChange}
                                            />
                                        ))}
                                    </div>
                                </div>
                                {textareaField({ label: "Médicaments", name: "medications", value: formData.medications, handleChange, placeholder: "Liste + dosages" })}
                                {textareaField({ label: "Allergies", name: "allergies", value: formData.allergies, handleChange })}
                                {textareaField({ label: "Conditions médicales", name: "medicalConditions", value: formData.medicalConditions, handleChange })}
                                {textareaField({ label: "Besoins de mobilité", name: "mobilityNeeds", value: formData.mobilityNeeds, handleChange })}
                            </div>

                            {/* Infos complémentaires */}
                            <SectionTitle icon={FileText} color="#A6CCE6" title="Informations complémentaires" />
                            <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                                {inputField({ label: "Restrictions alimentaires", name: "dietaryRestrictions", value: formData.dietaryRestrictions, handleChange })}
                                {inputField({ label: "Intérêts / loisirs", name: "interests", value: formData.interests, handleChange })}
                                {textareaField({ label: "Demandes spéciales", name: "specialRequests", value: formData.specialRequests, handleChange })}
                            </div>

                            {/* Visite & Occupation */}
                            <SectionTitle icon={Calendar} color="#BAAED9" title="Visite & délais d'occupation" />
                            <div className="grid grid-cols-1 md:grid-cols-2 gap-6 mb-8">
                                {inputField({ label: "Date souhaitée de visite *", name: "visitDate", type: "date", value: formData.visitDate, handleChange })}
                                <div>
                                    <label className="block text-sm font-semibold text-gray-700 mb-2">Délais d'occupation souhaité *</label>
                                    <div className="grid grid-cols-2 gap-2">
                                        {occupancyOptions.map((opt) => (
                                            <RadioPill
                                                key={opt.value}
                                                name="occupancyDelay"
                                                value={opt.value}
                                                label={opt.label}
                                                checked={formData.occupancyDelay === opt.value}
                                                onChange={handleChange}
                                            />
                                        ))}
                                    </div>
                                </div>
                            </div>

                            {/* Dépendances */}
                            <SectionTitle icon={AlertCircle} color="#A6CCE6" title="Dépendances connues" />
                            <div className="space-y-6">
                                <YesNoRow
                                    icon={Cigarette}
                                    label="Fumeur"
                                    name="smoker"
                                    value={formData.smoker}
                                    onChange={handleChange}
                                />
                                <YesNoRow
                                    icon={Wine}
                                    label="Alcool"
                                    name="alcohol"
                                    value={formData.alcohol}
                                    onChange={handleChange}
                                />
                                <YesNoRow
                                    icon={AlertCircle}
                                    label="Autres dépendances"
                                    name="otherDependency"
                                    value={formData.otherDependency}
                                    onChange={handleChange}
                                />
                                {formData.otherDependency === "yes" && (
                                    <inputField label="Précisez la dépendance" name="otherDetails" value={formData.otherDetails} handleChange={handleChange} />
                                )}
                            </div>

                            {/* Documents */}
                            <SectionTitle icon={Shield} color="#BAAED9" title="Documents à apporter" />
                            <div className="bg-gray-50 rounded-xl p-6 space-y-4">
                                {checkboxField({ label: "Carte d'assurance maladie (RAMQ)", name: "hasHealthCard", checked: formData.hasHealthCard, handleChange })}
                                {checkboxField({ label: "Rapports médicaux récents", name: "hasMedicalReports", checked: formData.hasMedicalReports, handleChange })}
                                {checkboxField({ label: "Assurance supplémentaire (si applicable)", name: "hasInsurance", checked: formData.hasInsurance, handleChange })}
                            </div>

                            {/* CTA */}
                            <button
                                type="submit"
                                className="w-full bg-gradient-to-r from-[#A6CCE6] to-[#BAAED9] text-white font-semibold py-4 px-6 rounded-lg hover:shadow-granby-lg transition-all duration-300 flex items-center justify-center"
                            >
                                <Send size={20} className="mr-2" /> Envoyer la demande
                            </button>
                        </form>
                    )}
                </div>
            </section>
        </div>
    );
};

/*────────── Sub Components & Helpers ─────────*/
const SectionTitle = ({ icon: Icon, color, title }) => (
    <h3 className="text-2xl font-bold text-gray-900 mb-6 flex items-center">
        <Icon size={24} className="mr-3" style={{ color }} />
        {title}
    </h3>
);

const inputField = ({ label, name, value, handleChange, type = "text", placeholder = "" }) => (
    <div>
        <label className="block text-sm font-semibold text-gray-700 mb-2">{label}</label>
        <input
            type={type}
            name={name}
            required={label.includes("*")}
            value={value}
            onChange={handleChange}
            placeholder={placeholder}
            className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-granby-blue transition-colors"
        />
    </div>
);

const textareaField = ({ label, name, value, handleChange, placeholder = "" }) => (
    <div className="md:col-span-2">
        <label className="block text-sm font-semibold text-gray-700 mb-2">{label}</label>
        <textarea
            name={name}
            rows={3}
            value={value}
            onChange={handleChange}
            placeholder={placeholder}
            className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-granby-blue resize-none transition-colors"
        />
    </div>
);

const checkboxField = ({ label, name, checked, handleChange }) => (
    <label className="flex items-center space-x-3 cursor-pointer select-none">
        <input
            type="checkbox"
            name={name}
            checked={checked}
            onChange={handleChange}
            className="peer appearance-none w-5 h-5 border border-gray-400 rounded-md checked:bg-[#A6CCE6] checked:border-transparent transition-all"
        />
        <span className="text-gray-700 peer-checked:font-semibold">{label}</span>
    </label>
);

const RadioPill = ({ name, value, label, checked, onChange }) => (
    <label className="cursor-pointer select-none">
        <input
            type="radio"
            name={name}
            value={value}
            checked={checked}
            onChange={onChange}
            className="peer sr-only"
        />
        <span
            className="block px-4 py-2 text-center rounded-lg border border-gray-300 peer-checked:bg-[#A6CCE6] peer-checked:text-white transition-colors"
        >
            {label}
        </span>
    </label>
);

const YesNoRow = ({ icon: Icon, label, name, value, onChange }) => (
    <div className="flex items-center space-x-4">
        <Icon size={20} className="text-[#A6CCE6]" />
        <span className="w-48 text-gray-700 font-semibold">{label}</span>
        <div className="flex space-x-2">
            {[
                { val: "yes", lbl: "Oui" },
                { val: "no", lbl: "Non" }
            ].map((opt) => (
                <RadioPill
                    key={opt.val}
                    name={name}
                    value={opt.val}
                    label={opt.lbl}
                    checked={value === opt.val}
                    onChange={onChange}
                />
            ))}
        </div>
    </div>
);

export default Admission;
