import React from "react";
import { motion } from "framer-motion";

const Home = () => {
  return (
    <div>
      <section className="bg-gradient-granby text-white text-center py-24">
        <motion.h1
          className="text-4xl font-bold mb-4"
          initial={{ opacity: 0, y: 30 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.6 }}
        >
          Un Foyer de <br /> <span className="font-extrabold">Sérénité et Soin</span>
        </motion.h1>
        <motion.p
          className="max-w-xl mx-auto text-lg"
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.8, delay: 0.3 }}
        >
          Spécialisés en troubles neurocognitifs, nous offrons un environnement sécuritaire et bienveillant avec surveillance 24h/7.
        </motion.p>
      </section>
    </div>
  );
};

export default Home;
