import React from 'react';
import { Link } from 'react-router-dom';
import { motion } from 'framer-motion';
import {
  Shield,
  Heart,
  Users,
  Star,
  Phone,
  Calendar,
  ArrowRight,
  CheckCircle,
  Award
} from 'lucide-react';

const Home = () => {
  const services = [
    {
      title: "Sécurité et Tranquillité",
      description: "Surveillance 24h/24 avec système de sécurité avancé et équipe de professionnels formés.",
      icon: Shield,
      color: "granby-blue"
    },
    {
      title: "Engagement et Expertise",
      description: "Spécialisés en troubles neurocognitifs avec accompagnement jusqu'à la fin de vie.",
      icon: Award,
      color: "granby-violet"
    },
    {
      title: "Respect et Dignité",
      description: "Accompagnement bienveillant respectant l'individualité et l'histoire de chaque résident.",
      icon: Heart,
      color: "granby-blue"
    },
    {
      title: "Soins Personnalisés",
      description: "Adaptation de nos soins selon les besoins spécifiques et préférences de chacun.",
      icon: Users,
      color: "granby-violet"
    },
    {
      title: "Environnement Familial",
      description: "Ambiance réconfortante favorisant un sentiment d'appartenance et de sécurité.",
      icon: Heart,
      color: "granby-blue"
    }
  ];

  const includedServices = [
    "3 repas et 2 collations par jour",
    "Ménage et buanderie hebdomadaires",
    "Électricité, chauffage, eau chaude et câble",
    "Loisirs et activités adaptées",
    "Surveillance 24h/7",
    "Infirmier(ère) auxiliaires et préposés 7j/7"
  ];

  return (
    <div className="min-h-screen font-sans">
      <section className="relative flex items-center min-h-screen overflow-hidden">
        <div
          className="absolute inset-0 bg-cover bg-center"
          style={{
            backgroundImage:
              "linear-gradient(135deg, rgba(166, 204, 230, 0.9), rgba(186, 174, 217, 0.9)), url('https://images.pexels.com/photos/7544762/pexels-photo-7544762.jpeg')"
          }}
        />

        <div className="relative z-10 max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-20">
          <div className="max-w-3xl">
            <motion.div
              initial={{ opacity: 0, y: 30 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.8 }}
            >
              <h1 className="text-5xl lg:text-7xl font-merriweather text-white leading-tight mb-6">
                Un Foyer de <br />
                <span className="block bg-gradient-to-r from-white to-gray-200 bg-clip-text text-transparent">
                  Sérénité et Soin
                </span>
              </h1>

              <p className="text-xl lg:text-2xl font-merriweather text-white/90 mb-8 leading-relaxed">
                Spécialisés en troubles neurocognitifs, nous offrons un environnement sécuritaire et bienveillant avec surveillance 24h/7, dans une ambiance familiale et respectueuse.
              </p>

              <div className="flex flex-col sm:flex-row gap-4">
                <Link to="/contact">
                  <span className="inline-flex items-center px-8 py-4 bg-white text-granby-blue font-merriweather text-base font-semibold rounded-xl hover:bg-gray-50 transition-all duration-300 shadow-granby-lg hover:shadow-xl group">
                    Planifier une visite →
                  </span>
                </Link>

                <a
                  className="inline-flex items-center px-8 py-4 text-white border border-white rounded-xl font-merriweather text-base font-semibold hover:bg-white hover:text-granby-blue transition-all duration-300">
                  <Phone size={20} className="mr-2" />
                  (450) 555-0123
                </a>
              </div>
            </motion.div>
          </div>
        </div>
      </section>

      <section className="py-20 bg-gray-50">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <motion.div
            initial={{ opacity: 0, y: 30 }}
            whileInView={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.8 }}
            className="text-center mb-16"
          >
            <h2 className="text-4xl lg:text-5xl font-playfair text-gray-900 mb-6">
              Nos Valeurs Fondamentales
            </h2>
            <p className="text-xl font-sans text-gray-600 max-w-3xl mx-auto">
              Cinq piliers qui guident notre approche pour offrir les meilleurs soins à vos proches dans le respect et la dignité.
            </p>
          </motion.div>

          <div className="flex flex-wrap justify-center gap-8">
            {services.map((service, index) => {
              const IconComponent = service.icon;
              return (
                <motion.div
                  key={service.title}
                  initial={{ opacity: 0, y: 30 }}
                  whileInView={{ opacity: 1, y: 0 }}
                  transition={{ duration: 0.6, delay: index * 0.1 }}
                  className="w-full sm:w-[300px] md:w-[300px] group"
                >
                  <div className="bg-white rounded-2xl p-8 shadow-granby hover:shadow-granby-lg transition-all duration-300 h-full hover:scale-105">
                    <div className={`w-16 h-16 bg-${service.color}/20 rounded-xl flex items-center justify-center mb-6 group-hover:scale-110 transition-transform duration-300`}>
                      <IconComponent size={32} className={`text-${service.color}`} />
                    </div>
                    <h3 className="text-xl font-playfair text-gray-900 mb-4">
                      {service.title}
                    </h3>
                    <p className="text-gray-600 font-sans leading-relaxed">
                      {service.description}
                    </p>
                  </div>
                </motion.div>
              );
            })}
          </div>
        </div>
      </section>

      {/* Services Inclus Section */}
      <section className="py-20">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="grid grid-cols-1 lg:grid-cols-2 gap-16 items-center">
            <motion.div
              initial={{ opacity: 0, x: -30 }}
              whileInView={{ opacity: 1, x: 0 }}
              transition={{ duration: 0.8 }}
            >
              <h2 className="text-4xl lg:text-5xl font-playfair text-gray-900 mb-8">
                Services Inclus
              </h2>
              <p className="text-xl text-gray-600 font-sans mb-8">
                Tout ce dont vos proches ont besoin pour une vie confortable et sécuritaire.
              </p>
              <div className="space-y-4">
                {includedServices.map((service, index) => (
                  <motion.div
                    key={index}
                    initial={{ opacity: 0, x: -20 }}
                    whileInView={{ opacity: 1, x: 0 }}
                    transition={{ duration: 0.5, delay: index * 0.1 }}
                    className="flex items-center space-x-3"
                  >
                    <CheckCircle className="text-granby-blue flex-shrink-0" size={24} />
                    <span className="text-gray-700 text-lg font-sans">{service}</span>
                  </motion.div>
                ))}
              </div>
            </motion.div>
            <motion.div
              initial={{ opacity: 0, x: 30 }}
              whileInView={{ opacity: 1, x: 0 }}
              transition={{ duration: 0.8 }}
              className="relative"
            >
              <img
                src="https://images.unsplash.com/photo-1536064479547-7ee40b74b807"
                alt="Professionnel de santé"
                className="rounded-2xl shadow-granby-lg w-full h-96 object-cover"
              />
            </motion.div>
          </div>
        </div>
      </section>

    </div>
  );
};

export default Home;
