import React from 'react';
import { Link, useLocation } from 'react-router-dom';
import { Phone, MapPin, Clock, Mail, Menu, X } from 'lucide-react';
import { motion, AnimatePresence } from 'framer-motion';

const Layout = ({ children }) => {
  const location = useLocation();
  const [isMenuOpen, setIsMenuOpen] = React.useState(false);

  const navigation = [
    { name: 'Accueil', href: '/' },
    { name: 'Qui sommes nous', href: '/about' },
    { name: 'Contact', href: '/contact' },
    { name: 'Candidature', href: '/application' },
    { name: 'Carrières', href: '/careers' },
  ];

  const isActive = (path) => location.pathname === path;

  return (
    <div className="min-h-screen bg-white font-sans">
      {/* Header */}
      <header className="relative bg-white/95 backdrop-blur-sm border-b border-granby-blue/20 sticky top-0 z-50 shadow-sm">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex justify-between items-center py-0">
            {/* Logo */}
            <Link to="/" className="flex items-center space-x-4">
              {/* Logo Lotus */}
              <div className="w-20 h-20 rounded-full overflow-hidden shadow-lg">
                <img
                  src="/logoRSA_lotus.png"
                  alt="Logo Résidence St-Antoine"
                  className="w-full h-full object-contain"
                />
              </div>

              {/* Logo avec le nom, très agrandi */}
              <div className="h-32


              ">
                <img
                  src="/logoRSA_name.png"
                  alt="Résidence St-Antoine"
                  className="h-full w-auto object-contain"
                />
              </div>
            </Link>



            {/* Desktop Navigation */}
            <nav className="hidden md:flex space-x-8">
              {navigation.map((item) => (
                <Link
                  key={item.name}
                  to={item.href}
                  className={`px-4 py-2 rounded-lg text-sm font-medium transition-all duration-300 font-athelas ${isActive(item.href)
                    ? 'bg-gradient-granby text-white shadow-granby'
                    : 'text-gray-700 hover:text-granby-blue hover:bg-granby-blue/10'
                    }`}
                >
                  {item.name}
                </Link>
              ))}
            </nav>

            {/* Mobile menu button */}
            <button
              onClick={() => setIsMenuOpen(!isMenuOpen)}
              className="md:hidden p-2 rounded-lg text-gray-700 hover:bg-granby-blue/10"
            >
              {isMenuOpen ? <X size={24} /> : <Menu size={24} />}
            </button>
          </div>
        </div>

        {/* Mobile Navigation */}
        <AnimatePresence>
          {isMenuOpen && (
            <motion.div
              initial={{ opacity: 0, height: 0 }}
              animate={{ opacity: 1, height: 'auto' }}
              exit={{ opacity: 0, height: 0 }}
              className="md:hidden bg-white border-t border-granby-blue/20"
            >
              <div className="px-4 py-4 space-y-2">
                {navigation.map((item) => (
                  <Link
                    key={item.name}
                    to={item.href}
                    onClick={() => setIsMenuOpen(false)}
                    className={`block px-4 py-3 rounded-lg text-base font-athelas transition-all duration-300 ${isActive(item.href)
                      ? 'bg-gradient-granby text-white'
                      : 'text-gray-700 hover:bg-granby-blue/10'
                      }`}
                  >
                    {item.name}
                  </Link>
                ))}
              </div>
            </motion.div>
          )}
        </AnimatePresence>
      </header>

      {/* Main Content */}
      <main>
        {children}
      </main>

      {/* Footer */}
      <footer className="bg-gray-900 text-white">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-16">
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-8">
            {/* Company Info */}
            <div className="lg:col-span-2">
              <div className="flex items-center space-x-3 mb-6">
                <div className="w-12 h-12 bg-gradient-granby rounded-xl flex items-center justify-center">
                  <span className="text-2xl font-bold text-white">RG</span>
                </div>
                <div>
                  <h3 className="text-xl font-bold">Résidence Granby</h3>
                  <p className="text-granby-blue">Soins spécialisés en troubles neurocognitifs</p>
                </div>
              </div>
              <p className="text-gray-300 mb-6 leading-relaxed">
                Spécialisés dans les troubles neurocognitifs, nous offrons un environnement sécuritaire
                et bienveillant avec surveillance 24h/7, dans une ambiance familiale et respectueuse.
              </p>
              <div className="flex space-x-4">
                <div className="w-10 h-10 bg-granby-blue/20 rounded-lg flex items-center justify-center hover:bg-granby-blue/30 transition-colors cursor-pointer">
                  <Phone size={20} className="text-granby-blue" />
                </div>
                <div className="w-10 h-10 bg-granby-violet/20 rounded-lg flex items-center justify-center hover:bg-granby-violet/30 transition-colors cursor-pointer">
                  <Mail size={20} className="text-granby-violet" />
                </div>
              </div>
            </div>

            {/* Quick Links */}
            <div>
              <h4 className="text-lg font-semibold mb-6">Navigation</h4>
              <ul className="space-y-3">
                {navigation.map((item) => (
                  <li key={item.name}>
                    <Link
                      to={item.href}
                      className="text-gray-300 hover:text-granby-blue transition-colors"
                    >
                      {item.name}
                    </Link>
                  </li>
                ))}
              </ul>
            </div>

            {/* Contact Info */}
            <div>
              <h4 className="text-lg font-semibold mb-6">Contact</h4>
              <div className="space-y-4">
                <div className="flex items-start space-x-3">
                  <MapPin size={20} className="text-granby-blue mt-1 flex-shrink-0" />
                  <div>
                    <p className="text-gray-300">123 Rue Principale</p>
                    <p className="text-gray-300">Granby, QC J2G 1A1</p>
                  </div>
                </div>
                <div className="flex items-center space-x-3">
                  <Phone size={20} className="text-granby-blue flex-shrink-0" />
                  <p className="text-gray-300">(450) 555-0123</p>
                </div>
                <div className="flex items-center space-x-3">
                  <Clock size={20} className="text-granby-violet flex-shrink-0" />
                  <p className="text-gray-300">24h/7 Surveillance</p>
                </div>
              </div>
            </div>
          </div>

          <div className="border-t border-gray-800 mt-12 pt-8 text-center">
            <p className="text-gray-400">
              © 2024 Résidence Granby. Tous droits réservés. | Conçu avec soin pour vos proches.
            </p>
          </div>
        </div>
      </footer>
    </div >
  );
};

export default Layout;
