import React, { useState } from 'react';
import { motion } from 'framer-motion';
import {
    User,
    Heart,
    Phone,
    Mail,
    MapPin,
    Clock,
    Send,
    CheckCircle,
    Check
} from 'lucide-react';

/* -------------------------------------------------------------------------- */
/*  UTILITAIRES DE STYLE : cases carrées avec animation check + fond bleu     */
/* -------------------------------------------------------------------------- */
// 1) Tailwind utilise la classe "peer" pour cibler l'état :
//    <input type="checkbox" className="peer" /> et le <span> frère peut
//    réagir via peer-checked:*                                       
// 2) On masque l'input visuellement (opacity-0 absolute) pour conserver l’accessibilité
// 3) Le carré est géré via un <span> avec bordure, fond & icône Check animés

const SquareCheck = ({
    name,
    value,
    checked,
    onChange,
    children,
    required = false,
}) => (
    <label className="flex items-center space-x-2 cursor-pointer select-none">
        <input
            type="radio"
            name={name}
            value={value}
            checked={checked}
            onChange={onChange}
            required={required}
            className="sr-only peer"
        />
        {/* Carré */}
        <span
            className="w-5 h-5 border-2 border-[#84B8D9] rounded-sm flex items-center justify-center
                 transition-all duration-200 peer-checked:bg-[#E6F2F9]"
        >
            <Check
                size={16}
                className="text-[#84B8D9] scale-0 transition-transform duration-200 peer-checked:scale-100"
            />
        </span>
        <span className="text-sm">{children}</span>
    </label>
);

const SquareYesNo = ({ name, value, state, onChange, required = false }) => (
    <div className="flex items-center space-x-6">
        {['Non', 'Oui'].map((v) => (
            <SquareCheck
                key={v}
                name={name}
                value={v}
                required={required}
                checked={state === v}
                onChange={onChange}
            >
                {v}
            </SquareCheck>
        ))}
    </div>
);

/* -------------------------------------------------------------------------- */

const Admission = () => {
    const [formData, setFormData] = useState({
        /* Identité résident */
        residentLastName: '',
        residentFirstName: '',
        residentDob: '',
        moveInDelay: '',

        /* Personne respo */
        contactLastName: '',
        contactFirstName: '',
        contactPhone: '',
        contactEmail: '',

        /* Soins / dépendances */
        mobilityAid: '',
        neuro: '',
        neuroDetails: '',
        pets: '',
        allergies: '',
        dependencies: {
            smoking: '',
            alcohol: '',
            other: '',
            otherDetails: '',
        },

        /* AVQ */
        avq: {},

        comments: '',
    });

    const [isSubmitted, setIsSubmitted] = useState(false);

    /* ------------------------------- handlers -------------------------------- */

    const handleChange = (e) => {
        const { name, value, type } = e.target;

        // Dépendances connues imbriquées
        if (name.startsWith('dep_')) {
            const depKey = name.replace('dep_', '');
            setFormData((prev) => ({
                ...prev,
                dependencies: {
                    ...prev.dependencies,
                    [depKey]: value,
                },
            }));
            return;
        }

        // AVQ (radio Oui/Non)
        if (name.startsWith('avq_')) {
            const avqKey = name.replace('avq_', '');
            setFormData((prev) => ({
                ...prev,
                avq: {
                    ...prev.avq,
                    [avqKey]: value,
                },
            }));
            return;
        }

        setFormData((prev) => ({
            ...prev,
            [name]: type === 'checkbox' ? e.target.checked : value,
        }));
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        // Ici on enverrait vers un backend…
        setIsSubmitted(true);
        setTimeout(() => setIsSubmitted(false), 5000);
    };

    /* ------------------------------ constantes ------------------------------ */

    const contactInfo = [
        {
            icon: Phone,
            title: 'Téléphone',
            details: '(450) 555-0123',
            description: 'Disponible 24h/7 pour urgences',
            iconColor: '#84B8D9',
            bgColor: '#F1F8FC',
        },
        {
            icon: Mail,
            title: 'Email',
            details: 'info@residencegranby.ca',
            description: 'Réponse sous 24h',
            iconColor: '#B7A5D7',
            bgColor: '#F8F5FC',
        },
        {
            icon: MapPin,
            title: 'Adresse',
            details: '123 Rue Principale, Granby, QC',
            description: "Stationnement gratuit sur place",
            iconColor: '#84B8D9',
            bgColor: '#F1F8FC',
        },
        {
            icon: Clock,
            title: 'Heures de visite',
            details: 'Lundi → Dimanche ⁄ 9h → 18h',
            description: 'Rendez-vous recommandés',
            iconColor: '#B7A5D7',
            bgColor: '#F8F5FC',
        },
    ];

    const mobilityOptions = [
        'Se déplace seul(e)',
        'Canne',
        'Manchette',
        'Déambulateur',
        'Fauteuil roulant',
    ];

    const avqItems = [
        'Toilette partielle matin/soir',
        'Prendre un bain',
        'Se nourrir',
        'Se vêtir',
        'Prendre / gérer les médicaments',
        'Continence',
    ];

    const moveInDelays = [
        'Immédiat – 2 semaines',
        '2 – 4 semaines',
        '1 – 2 mois',
        'Plus de 2 mois',
    ];

    /* -------------------------------- render -------------------------------- */

    return (
        <div className="font-merriweather">
            {/* ------------------------------- HERO ------------------------------- */}
            <section className="relative min-h-[60vh] flex items-center justify-center">
                <div
                    className="absolute inset-0 bg-cover bg-center"
                    style={{
                        backgroundImage:
                            "linear-gradient(135deg, rgba(166,204,230,0.9), rgba(186,174,217,0.9)), url('https://images.pexels.com/photos/8728559/pexels-photo-8728559.jpeg')",
                    }}
                />
                <div className="relative z-10 text-white text-center space-y-6 px-4">
                    <motion.h1
                        initial={{ opacity: 0, y: 30 }}
                        animate={{ opacity: 1, y: 0 }}
                        transition={{ duration: 0.8 }}
                        className="text-5xl font-playfair font-bold"
                    >
                        Contact&nbsp;et&nbsp;Admission
                    </motion.h1>
                    <motion.p
                        initial={{ opacity: 0, y: 30 }}
                        animate={{ opacity: 1, y: 0 }}
                        transition={{ delay: 0.3, duration: 0.8 }}
                        className="text-xl text-white/90 max-w-3xl mx-auto"
                    >
                        Vous souhaitez faire une demande d&apos;admission&nbsp;? Commencez
                        ici&nbsp;: notre équipe vous recontactera rapidement.
                    </motion.p>
                </div>
            </section>

            {/* --------------------------- INFOS CONTACT --------------------------- */}
            <section className="py-20 bg-white">
                <div className="max-w-7xl mx-auto px-4 grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-8">
                    {contactInfo.map(
                        ({ icon: Icon, title, details, description, iconColor, bgColor }, i) => (
                            <motion.div
                                key={title}
                                initial={{ opacity: 0, y: 30 }}
                                whileInView={{ opacity: 1, y: 0 }}
                                transition={{ duration: 0.6, delay: i * 0.1 }}
                                className="text-center"
                            >
                                <div
                                    className="w-16 h-16 rounded-xl flex items-center justify-center mx-auto mb-4"
                                    style={{ backgroundColor: bgColor }}
                                >
                                    <Icon size={32} style={{ color: iconColor }} />
                                </div>
                                <h3 className="text-xl font-bold text-gray-900 mb-2">{title}</h3>
                                <p className="text-gray-700 font-medium mb-2">{details}</p>
                                <p className="text-gray-500 text-sm">{description}</p>
                            </motion.div>
                        )
                    )}
                </div>
            </section>

            {/* ----------------------------- FORMULAIRE ---------------------------- */}
            <section className="py-20 bg-gradient-to-b from-[#A6CCE6]/20 to-white">
                <div className="max-w-4xl mx-auto bg-white rounded-2xl shadow-granby-lg p-10">
                    <motion.h2
                        initial={{ opacity: 0, y: 30 }}
                        whileInView={{ opacity: 1, y: 0 }}
                        transition={{ duration: 0.8 }}
                        className="text-4xl font-playfair font-bold text-center text-gray-900 mb-10"
                    >
                        Demande dʼadmission pour un proche
                    </motion.h2>

                    {/* --------------------- MESSAGE DE CONFIRMATION -------------------- */}
                    {isSubmitted ? (
                        <div className="text-center py-10">
                            <CheckCircle className="text-[#84B8D9] mx-auto mb-4" size={60} />
                            <h3 className="text-2xl font-bold text-gray-900 mb-4">
                                Merci de nous considérer !
                            </h3>
                            <p className="text-gray-700 max-w-lg mx-auto">
                                Merci de nous considérer comme un choix de milieu de vie pour
                                votre proche. Un membre de l’équipe vous contactera dans les
                                prochains&nbsp;48&nbsp;heures.
                            </p>
                        </div>
                    ) : (
                        /* --------------------------- FORM --------------------------- */
                        <form onSubmit={handleSubmit} className="space-y-12">
                            {/* ---------- SECTION 1 : IDENTITÉ RÉSIDENT ---------- */}
                            <div>
                                <h3 className="flex items-center text-2xl font-playfair font-semibold text-gray-800 mb-6">
                                    <User className="mr-2 text-[#84B8D9]" />
                                    Informations personnelles
                                </h3>

                                <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                                    <div>
                                        <label className="block text-sm font-semibold text-gray-700 mb-2">
                                            Nom *
                                        </label>
                                        <input
                                            type="text"
                                            name="residentLastName"
                                            required
                                            value={formData.residentLastName}
                                            onChange={handleChange}
                                            className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-[#84B8D9]"
                                            placeholder="Nom de famille"
                                        />
                                    </div>
                                    <div>
                                        <label className="block text-sm font-semibold text-gray-700 mb-2">
                                            Prénom *
                                        </label>
                                        <input
                                            type="text"
                                            name="residentFirstName"
                                            required
                                            value={formData.residentFirstName}
                                            onChange={handleChange}
                                            className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-[#84B8D9]"
                                            placeholder="Prénom"
                                        />
                                    </div>
                                    <div>
                                        <label className="block text-sm font-semibold text-gray-700 mb-2">
                                            Date de naissance *
                                        </label>
                                        <input
                                            type="date"
                                            name="residentDob"
                                            required
                                            value={formData.residentDob}
                                            onChange={handleChange}
                                            className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-[#84B8D9]"
                                        />
                                    </div>
                                    {/* ---------- délai d’occupation souhaité ---------- */}
                                    <div>
                                        <label className="block text-sm font-semibold text-gray-700 mb-2">
                                            Délais d’occupation souhaité *
                                        </label>

                                        <select
                                            name="moveInDelay"
                                            required
                                            value={formData.moveInDelay}
                                            onChange={handleChange}
                                            className="w-full px-4 py-3 border border-gray-300 rounded-lg 
               focus:ring-2 focus:ring-[#84B8D9] bg-white text-gray-700"
                                        >
                                            <option value="" disabled>
                                                — Sélectionnez un délai —
                                            </option>
                                            {moveInDelays.map((opt) => (
                                                <option key={opt} value={opt}>
                                                    {opt}
                                                </option>
                                            ))}
                                        </select>
                                    </div>
                                </div>
                            </div>

                            {/* ---------- SECTION 2 : PERSONNE RESPONSABLE ---------- */}
                            <div>
                                <h3 className="flex items-center text-2xl font-playfair font-semibold text-gray-800 mb-6">
                                    <Phone className="mr-2 text-[#84B8D9]" /> Personne responsable
                                </h3>

                                <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                                    {[
                                        {
                                            label: 'Nom *',
                                            name: 'contactLastName',
                                            placeholder: 'Nom',
                                            type: 'text',
                                        },
                                        {
                                            label: 'Prénom *',
                                            name: 'contactFirstName',
                                            placeholder: 'Prénom',
                                            type: 'text',
                                        },
                                        {
                                            label: 'Téléphone *',
                                            name: 'contactPhone',
                                            placeholder: '(450) 555-0123',
                                            type: 'tel',
                                        },
                                        {
                                            label: 'Courriel *',
                                            name: 'contactEmail',
                                            placeholder: 'contact@email.com',
                                            type: 'email',
                                        },
                                    ].map((f) => (
                                        <div key={f.name}>
                                            <label className="block text-sm font-semibold text-gray-700 mb-2">
                                                {f.label}
                                            </label>
                                            <input
                                                type={f.type}
                                                name={f.name}
                                                required
                                                value={formData[f.name]}
                                                onChange={handleChange}
                                                placeholder={f.placeholder}
                                                className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-[#84B8D9]"
                                            />
                                        </div>
                                    ))}
                                </div>
                            </div>

                            {/* ---------- SECTION 3 : SOINS & DÉPENDANCES ---------- */}
                            <div>
                                <h3 className="flex items-center text-2xl font-playfair font-semibold text-gray-800 mb-6">
                                    <Heart className="mr-2 text-[#84B8D9]" /> Besoins de soins et
                                    services
                                </h3>

                                {/* ­— MOBILITÉ */}
                                <div className="mb-10">
                                    <p className="font-semibold mb-3">Déplacement physique *</p>
                                    <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
                                        {mobilityOptions.map((opt) => (
                                            <SquareCheck
                                                key={opt}
                                                name="mobilityAid"
                                                value={opt}
                                                checked={formData.mobilityAid === opt}
                                                onChange={handleChange}
                                                required
                                            >
                                                {opt}
                                            </SquareCheck>
                                        ))}
                                    </div>
                                </div>

                                {/* ­— TROUBLES COGNITIFS */}
                                <div className="mb-10">
                                    <p className="font-semibold mb-3">
                                        Diagnostic de troubles neurocognitifs ?
                                    </p>
                                    <SquareYesNo
                                        name="neuro"
                                        required
                                        state={formData.neuro}
                                        onChange={handleChange}
                                    />
                                    {formData.neuro === 'Oui' && (
                                        <input
                                            type="text"
                                            name="neuroDetails"
                                            value={formData.neuroDetails}
                                            onChange={handleChange}
                                            className="mt-3 w-full px-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-[#84B8D9]"
                                            placeholder="Préciser le diagnostic (ex. Alzheimer)"
                                        />
                                    )}
                                </div>

                                {/* ­— DÉPENDANCES CONNUES */}
                                <div className="mb-10">
                                    <p className="font-semibold mb-3">Dépendances connues</p>

                                    {[
                                        ['smoking', 'Fumeur'],
                                        ['alcohol', 'Alcool'],
                                        ['other', 'Autres'],
                                    ].map(([key, label]) => (
                                        <div key={key} className="mb-4">
                                            <span className="text-sm mr-4">{label}</span>
                                            <SquareYesNo
                                                name={`dep_${key}`}
                                                state={formData.dependencies[key]}
                                                onChange={handleChange}
                                            />
                                            {key === 'other' &&
                                                formData.dependencies.other === 'Oui' && (
                                                    <input
                                                        type="text"
                                                        name="dep_otherDetails"
                                                        value={formData.dependencies.otherDetails}
                                                        onChange={(e) =>
                                                            setFormData((prev) => ({
                                                                ...prev,
                                                                dependencies: {
                                                                    ...prev.dependencies,
                                                                    otherDetails: e.target.value,
                                                                },
                                                            }))
                                                        }
                                                        className="mt-2 w-full px-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-[#84B8D9]"
                                                        placeholder="Précisez la dépendance"
                                                    />
                                                )}
                                        </div>
                                    ))}
                                </div>

                                {/* ­— AVQ */}
                                <div className="mb-10">
                                    <p className="font-semibold mb-4">
                                        Activités de vie quotidienne (Oui = aide nécessaire)
                                    </p>
                                    {avqItems.map((item) => (
                                        <div
                                            key={item}
                                            className="grid grid-cols-1 md:grid-cols-3 gap-4 items-center mb-3"
                                        >
                                            <span className="text-sm">{item}</span>
                                            <SquareCheck
                                                name={`avq_${item}`}
                                                value="Non"
                                                checked={formData.avq[item] === 'Non'}
                                                onChange={handleChange}
                                            >
                                                Non
                                            </SquareCheck>
                                            <SquareCheck
                                                name={`avq_${item}`}
                                                value="Oui"
                                                checked={formData.avq[item] === 'Oui'}
                                                onChange={handleChange}
                                            >
                                                Oui
                                            </SquareCheck>
                                        </div>
                                    ))}
                                </div>

                                {/* ­— AUTRES CHAMPS */}
                                <div className="grid grid-cols-1 md:grid-cols-2 gap-6 mb-8">
                                    {/* Animal */}
                                    <div>
                                        <p className="font-semibold mb-2">Animal de compagnie ?</p>
                                        <SquareYesNo
                                            name="pets"
                                            state={formData.pets}
                                            onChange={handleChange}
                                            required
                                        />
                                    </div>

                                    {/* Allergies */}
                                    <div>
                                        <label className="block text-sm font-semibold text-gray-700 mb-2">
                                            Allergies connues
                                        </label>
                                        <input
                                            type="text"
                                            name="allergies"
                                            value={formData.allergies}
                                            onChange={handleChange}
                                            placeholder="Aucune / Précisez"
                                            className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-[#84B8D9]"
                                        />
                                    </div>
                                </div>

                                {/* Commentaires */}
                                <div>
                                    <label className="block text-sm font-semibold text-gray-700 mb-2">
                                        Demandes spéciales / Commentaires
                                    </label>
                                    <textarea
                                        name="comments"
                                        rows={4}
                                        value={formData.comments}
                                        onChange={handleChange}
                                        className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-[#84B8D9] resize-none"
                                        placeholder="Toute information supplémentaire pertinente"
                                    />
                                </div>
                            </div>

                            {/* ---------- BOUTON ENVOI ---------- */}
                            <button
                                type="submit"
                                className="w-full bg-gradient-to-r from-[#84B8D9] to-[#B7A5D7] text-white font-semibold py-4 px-6 rounded-lg
                           hover:shadow-lg transition-all duration-300 flex items-center justify-center"
                            >
                                <Send size={20} className="mr-2" />
                                Envoyer la demande
                            </button>
                        </form>
                    )}
                </div>
            </section>
        </div>
    );
};

export default Admission;
