import React, { useState } from 'react';
import { motion } from 'framer-motion';
import {
    User,
    Heart,
    Phone,
    Mail,
    MapPin,
    Clock,
    Send,
    CheckCircle,
    Check
} from 'lucide-react';


/* -------------------------------------------------------------------------- */
/*  Cases carrées + check animé                                               */
/* -------------------------------------------------------------------------- */
/* -------------------------------------------------------------------------- */
/*  Case carrée + check visible avec opacity                                   */
/* -------------------------------------------------------------------------- */
const SquareCheck = ({
    name,
    value,
    checked,
    onChange,
    children,
    required = false,
    type = "radio",          // tu peux passer "checkbox" si besoin
}) => (
    <label className="flex items-center space-x-3 cursor-pointer select-none">
        <input
            type={type}
            name={name}
            value={value}
            checked={checked}
            onChange={onChange}
            required={required}
            className="sr-only peer"
        />

        <span
            className="w-6 h-6 border-2 border-[#84B8D9] rounded-sm flex items-center justify-center
                   transition-colors duration-200
                   peer-checked:bg-[#E6F2F9] peer-checked:border-[#84B8D9]"
        >
            {/* ✔️ : caché par défaut, visible quand coché */}
            <Check
                size={20}
                className="text-[#84B8D9] opacity-0 transition-opacity duration-200
                     peer-checked:opacity-100 pointer-events-none"
            />
        </span>

        <span className="text-base">{children}</span>
    </label>
);

const SquareYesNo = ({ name, state, onChange, required = false }) => (
    <div className="flex items-center space-x-10">
        {['Non', 'Oui'].map((v) => (
            <SquareCheck
                key={v}
                name={name}
                value={v}
                required={required}
                checked={state === v}
                onChange={onChange}
            >
                {v}
            </SquareCheck>
        ))}
    </div>
);

/* -------------------------------------------------------------------------- */

const Admission = () => {
    const [formData, setFormData] = useState({
        residentLastName: '',
        residentFirstName: '',
        residentDob: '',
        moveInDelay: '',
        contactLastName: '',
        contactFirstName: '',
        contactPhone: '',
        contactEmail: '',
        mobilityAid: '',
        neuro: '',
        neuroDetails: '',
        pets: '',
        allergies: '',
        dependencies: {
            smoking: '',
            alcohol: '',
            other: '',
            otherDetails: '',
        },
        avq: {},
        comments: '',
    });
    const [isSubmitted, setIsSubmitted] = useState(false);

    /* ---------------------------- Handlers ---------------------------- */
    const handleChange = (e) => {
        const { name, value, type } = e.target;

        if (name.startsWith('dep_')) {
            const depKey = name.replace('dep_', '');
            setFormData((prev) => ({
                ...prev,
                dependencies: { ...prev.dependencies, [depKey]: value },
            }));
            return;
        }
        if (name.startsWith('avq_')) {
            const key = name.replace('avq_', '');
            setFormData((prev) => ({
                ...prev,
                avq: { ...prev.avq, [key]: value },
            }));
            return;
        }
        setFormData((prev) => ({
            ...prev,
            [name]: type === 'checkbox' ? e.target.checked : value,
        }));
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        setIsSubmitted(true);
        setTimeout(() => setIsSubmitted(false), 5000);
    };

    /* ------------------------ Données statiques ------------------------ */
    const contactInfo = [
        {
            icon: Phone,
            title: 'Téléphone',
            details: '(450) 555-0123',
            description: 'Disponible 24 h / 7',
            iconColor: '#84B8D9',
            bgColor: '#F1F8FC',
        },
        {
            icon: Mail,
            title: 'Email',
            details: 'info@residencegranby.ca',
            description: 'Réponse sous 24 h',
            iconColor: '#B7A5D7',
            bgColor: '#F8F5FC',
        },
        {
            icon: MapPin,
            title: 'Adresse',
            details: '123 Rue Principale, Granby',
            description: 'Stationnement gratuit',
            iconColor: '#84B8D9',
            bgColor: '#F1F8FC',
        },
        {
            icon: Clock,
            title: 'Heures de visite',
            details: '7 j / 7 – 9 h → 18 h',
            description: 'Sur rendez-vous',
            iconColor: '#B7A5D7',
            bgColor: '#F8F5FC',
        },
    ];

    const mobilityOptions = [
        'Se déplace seul(e)',
        'Canne',
        'Manchette',
        'Déambulateur',
        'Fauteuil roulant',
    ];

    const avqItems = [
        'Toilette partielle matin/soir',
        'Prendre un bain',
        'Se nourrir',
        'Se vêtir',
        'Prendre / gérer les médicaments',
        'Continence',
    ];

    const moveInDelays = [
        'Immédiat – 2 sem.',
        '2 – 4 sem.',
        '1 – 2 mois',
        'Plus de 2 mois',
    ];

    /* ------------------------------- JSX ------------------------------- */
    return (
        <div className="font-merriweather text-[17px]">
            {/* HERO */}
            <section className="relative min-h-[60vh] flex items-center justify-center">
                <div
                    className="absolute inset-0 bg-cover bg-center"
                    style={{
                        backgroundImage:
                            "linear-gradient(135deg, rgba(166,204,230,0.9), rgba(186,174,217,0.9)), url('https://images.pexels.com/photos/8728559/pexels-photo-8728559.jpeg')",
                    }}
                />
                <div className="relative z-10 text-white text-center space-y-6 px-4">
                    <motion.h1
                        initial={{ opacity: 0, y: 30 }}
                        animate={{ opacity: 1, y: 0 }}
                        transition={{ duration: 0.8 }}
                        className="text-6xl font-playfair font-bold"
                    >
                        Contact et Admission
                    </motion.h1>
                    <motion.p
                        initial={{ opacity: 0, y: 30 }}
                        animate={{ opacity: 1, y: 0 }}
                        transition={{ delay: 0.3, duration: 0.8 }}
                        className="text-2xl max-w-3xl mx-auto text-white/90"
                    >
                        Débutez votre demande ; notre équipe vous rappellera rapidement.
                    </motion.p>
                </div>
            </section>

            {/* INFOS CONTACT */}
            <section className="py-24 bg-white">
                <div className="max-w-7xl mx-auto px-5 grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-10">
                    {contactInfo.map(({ icon: Icon, ...c }, i) => (
                        <motion.div
                            key={c.title}
                            initial={{ opacity: 0, y: 30 }}
                            whileInView={{ opacity: 1, y: 0 }}
                            transition={{ duration: 0.6, delay: i * 0.1 }}
                            className="text-center space-y-2"
                        >
                            <div
                                className="w-20 h-20 rounded-xl flex items-center justify-center mx-auto mb-4"
                                style={{ backgroundColor: c.bgColor }}
                            >
                                <Icon size={40} style={{ color: c.iconColor }} />
                            </div>
                            <h3 className="text-2xl font-bold text-gray-900">{c.title}</h3>
                            <p className="font-semibold text-gray-700">{c.details}</p>
                            <p className="text-gray-500 text-base">{c.description}</p>
                        </motion.div>
                    ))}
                </div>
            </section>

            {/* FORMULAIRE */}
            <section className="py-24 bg-gradient-to-b from-[#A6CCE6]/20 to-white">
                <div className="max-w-6xl mx-auto bg-white rounded-2xl shadow-granby-lg p-14">
                    <motion.h2
                        initial={{ opacity: 0, y: 30 }}
                        whileInView={{ opacity: 1, y: 0 }}
                        transition={{ duration: 0.8 }}
                        className="text-5xl font-playfair font-bold text-center mb-14"
                    >
                        Demande d’admission
                    </motion.h2>

                    {/* CONFIRMATION */}
                    {isSubmitted ? (
                        <div className="text-center py-20">
                            <CheckCircle size={80} className="text-[#84B8D9] mx-auto mb-6" />
                            <h3 className="text-3xl font-bold mb-4">
                                Merci de nous considérer !
                            </h3>
                            <p className="max-w-xl mx-auto">
                                Un membre de l’équipe vous contactera dans les <strong>48 h</strong>.
                            </p>
                        </div>
                    ) : (
                        <form onSubmit={handleSubmit} className="space-y-16">
                            {/* SECTION 1 • Informations personnelles */}
                            <div>
                                <h3 className="flex items-center text-3xl font-semibold mb-8">
                                    <User className="mr-3 text-[#84B8D9]" size={28} />
                                    Informations personnelles
                                </h3>

                                <div className="grid grid-cols-1 md:grid-cols-2 gap-8">
                                    {[
                                        {
                                            label: 'Nom *',
                                            name: 'residentLastName',
                                            placeholder: 'Nom de famille',
                                        },
                                        {
                                            label: 'Prénom *',
                                            name: 'residentFirstName',
                                            placeholder: 'Prénom',
                                        },
                                    ].map((f) => (
                                        <div key={f.name}>
                                            <label className="block text-base font-semibold mb-2">
                                                {f.label}
                                            </label>
                                            <input
                                                type="text"
                                                name={f.name}
                                                required
                                                value={formData[f.name]}
                                                onChange={handleChange}
                                                placeholder={f.placeholder}
                                                className="w-full px-5 py-4 border border-gray-300 rounded-lg focus:ring-2 focus:ring-[#84B8D9]"
                                            />
                                        </div>
                                    ))}

                                    <div>
                                        <label className="block text-base font-semibold mb-2">
                                            Date de naissance *
                                        </label>
                                        <input
                                            type="date"
                                            name="residentDob"
                                            required
                                            value={formData.residentDob}
                                            onChange={handleChange}
                                            className="w-full px-5 py-4 border border-gray-300 rounded-lg focus:ring-2 focus:ring-[#84B8D9]"
                                        />
                                    </div>

                                    <div>
                                        <label className="block text-base font-semibold mb-2">
                                            Délais d’occupation souhaité *
                                        </label>
                                        <select
                                            name="moveInDelay"
                                            required
                                            value={formData.moveInDelay}
                                            onChange={handleChange}
                                            className="w-full px-5 py-4 border border-gray-300 rounded-lg
                                 focus:ring-2 focus:ring-[#84B8D9] bg-white"
                                        >
                                            <option value="" disabled>
                                                — Sélectionnez —
                                            </option>
                                            {moveInDelays.map((d) => (
                                                <option key={d}>{d}</option>
                                            ))}
                                        </select>
                                    </div>
                                </div>
                            </div>

                            {/* SECTION 2 • Personne responsable */}
                            <div>
                                <h3 className="flex items-center text-3xl font-semibold mb-8">
                                    <Phone className="mr-3 text-[#84B8D9]" size={28} />
                                    Personne responsable
                                </h3>

                                <div className="grid grid-cols-1 md:grid-cols-2 gap-8">
                                    {[
                                        {
                                            label: 'Nom *',
                                            name: 'contactLastName',
                                            placeholder: 'Nom',
                                        },
                                        {
                                            label: 'Prénom *',
                                            name: 'contactFirstName',
                                            placeholder: 'Prénom',
                                        },
                                        {
                                            label: 'Téléphone *',
                                            name: 'contactPhone',
                                            placeholder: '(450) 555-0123',
                                            type: 'tel',
                                        },
                                        {
                                            label: 'Courriel *',
                                            name: 'contactEmail',
                                            placeholder: 'contact@email.com',
                                            type: 'email',
                                        },
                                    ].map((f) => (
                                        <div key={f.name}>
                                            <label className="block text-base font-semibold mb-2">
                                                {f.label}
                                            </label>
                                            <input
                                                type={f.type || 'text'}
                                                name={f.name}
                                                required
                                                value={formData[f.name]}
                                                onChange={handleChange}
                                                placeholder={f.placeholder}
                                                className="w-full px-5 py-4 border border-gray-300 rounded-lg focus:ring-2 focus:ring-[#84B8D9]"
                                            />
                                        </div>
                                    ))}
                                </div>
                            </div>

                            {/* SECTION 3 • Soins & dépendances */}
                            <div>
                                <h3 className="flex items-center text-3xl font-semibold mb-8">
                                    <Heart className="mr-3 text-[#84B8D9]" size={28} />
                                    Besoins de soins et services
                                </h3>

                                {/* Mobilité */}
                                <div className="mb-12">
                                    <p className="font-semibold mb-4 text-lg">Déplacement physique *</p>
                                    <div className="grid grid-cols-1 md:grid-cols-3 gap-5">
                                        {mobilityOptions.map((opt) => (
                                            <SquareCheck
                                                key={opt}
                                                name="mobilityAid"
                                                value={opt}
                                                required
                                                checked={formData.mobilityAid === opt}
                                                onChange={handleChange}
                                            >
                                                {opt}
                                            </SquareCheck>
                                        ))}
                                    </div>
                                </div>

                                {/* Neuro */}
                                <div className="mb-12">
                                    <p className="font-semibold mb-4 text-lg">
                                        Diagnostic de troubles neurocognitifs ?
                                    </p>
                                    <SquareYesNo
                                        name="neuro"
                                        state={formData.neuro}
                                        onChange={handleChange}
                                        required
                                    />
                                    {formData.neuro === 'Oui' && (
                                        <input
                                            type="text"
                                            name="neuroDetails"
                                            value={formData.neuroDetails}
                                            onChange={handleChange}
                                            className="mt-4 w-full px-5 py-4 border border-gray-300 rounded-lg focus:ring-2 focus:ring-[#84B8D9]"
                                            placeholder="Précisez le diagnostic"
                                        />
                                    )}
                                </div>

                                {/* Dépendances */}
                                <div className="mb-12">
                                    <p className="font-semibold mb-4 text-lg">Dépendances connues</p>
                                    {[
                                        ['smoking', 'Fumeur'],
                                        ['alcohol', 'Alcool'],
                                        ['other', 'Autres'],
                                    ].map(([k, lbl]) => (
                                        <div key={k} className="mb-6">
                                            <span className="mr-5 text-base">{lbl}</span>
                                            <SquareYesNo
                                                name={`dep_${k}`}
                                                state={formData.dependencies[k]}
                                                onChange={handleChange}
                                            />
                                            {k === 'other' && formData.dependencies.other === 'Oui' && (
                                                <input
                                                    type="text"
                                                    name="dep_otherDetails"
                                                    value={formData.dependencies.otherDetails}
                                                    onChange={(e) =>
                                                        setFormData((p) => ({
                                                            ...p,
                                                            dependencies: {
                                                                ...p.dependencies,
                                                                otherDetails: e.target.value,
                                                            },
                                                        }))
                                                    }
                                                    className="mt-3 w-full px-5 py-4 border border-gray-300 rounded-lg focus:ring-2 focus:ring-[#84B8D9]"
                                                    placeholder="Précisez la dépendance"
                                                />
                                            )}
                                        </div>
                                    ))}
                                </div>

                                {/* AVQ */}
                                <div className="mb-12">
                                    <p className="font-semibold mb-5 text-lg">
                                        Activités de vie quotidienne (Oui = aide nécessaire)
                                    </p>
                                    {avqItems.map((item) => (
                                        <div
                                            key={item}
                                            className="grid grid-cols-1 md:grid-cols-3 gap-5 items-center mb-4"
                                        >
                                            <span>{item}</span>
                                            <SquareCheck
                                                name={`avq_${item}`}
                                                value="Non"
                                                checked={formData.avq[item] === 'Non'}
                                                onChange={handleChange}
                                            >
                                                Non
                                            </SquareCheck>
                                            <SquareCheck
                                                name={`avq_${item}`}
                                                value="Oui"
                                                checked={formData.avq[item] === 'Oui'}
                                                onChange={handleChange}
                                            >
                                                Oui
                                            </SquareCheck>
                                        </div>
                                    ))}
                                </div>

                                {/* Divers */}
                                <div className="grid grid-cols-1 md:grid-cols-2 gap-8 mb-12">
                                    <div>
                                        <p className="font-semibold mb-2 text-lg">Animal de compagnie ?</p>
                                        <SquareYesNo
                                            name="pets"
                                            state={formData.pets}
                                            onChange={handleChange}
                                            required
                                        />
                                    </div>
                                    <div>
                                        <label className="block font-semibold mb-2 text-lg">
                                            Allergies connues
                                        </label>
                                        <input
                                            type="text"
                                            name="allergies"
                                            value={formData.allergies}
                                            onChange={handleChange}
                                            placeholder="Aucune / Précisez"
                                            className="w-full px-5 py-4 border border-gray-300 rounded-lg focus:ring-2 focus:ring-[#84B8D9]"
                                        />
                                    </div>
                                </div>

                                {/* Commentaires */}
                                <div>
                                    <label className="block font-semibold mb-2 text-lg">
                                        Demandes spéciales / Commentaires
                                    </label>
                                    <textarea
                                        name="comments"
                                        rows={5}
                                        value={formData.comments}
                                        onChange={handleChange}
                                        placeholder="Toute information pertinente"
                                        className="w-full px-5 py-4 border border-gray-300 rounded-lg focus:ring-2 focus:ring-[#84B8D9] resize-none"
                                    />
                                </div>
                            </div>

                            {/* SUBMIT */}
                            <button
                                type="submit"
                                className="w-full bg-gradient-to-r from-[#84B8D9] to-[#B7A5D7] text-white font-semibold
                           py-5 px-6 rounded-lg flex items-center justify-center text-xl
                           hover:shadow-lg transition-all duration-300"
                            >
                                <Send size={24} className="mr-3" />
                                Envoyer la demande
                            </button>
                        </form>
                    )}
                </div>
            </section>
        </div>
    );
};

export default Admission;
