// src/Layout.jsx
import React from 'react';
import { Link, useLocation } from 'react-router-dom';
import { Phone, MapPin, Clock, Mail, Menu, X } from 'lucide-react';
import { motion, AnimatePresence } from 'framer-motion';

// üóÇÔ∏è Place tes fichiers logo dans /public ou /src/assets.
// Ici on suppose /public pour simplifier le build et √©viter les imports cass√©s.
const LOGO_MARK = '/logoRSA_lotus.png';
const LOGO_WORD = '/logoRSA_name.png';

const Layout = ({ children }) => {
  const location = useLocation();
  const [isMenuOpen, setIsMenuOpen] = React.useState(false);

  const navigation = [
    { name: 'Accueil', href: '/' },
    { name: 'Qui sommes-nous', href: '/about' },
    { name: 'Admission', href: '/admission' },
    { name: 'Carri√®res', href: '/careers' },
  ];

  const isActive = (path) => location.pathname === path;

  return (
    <div className="min-h-screen bg-white font-sans text-gray-900">
      {/* ---------- HEADER ---------- */}
      <header className="sticky top-0 z-50 bg-white/95 backdrop-blur-sm border-b border-granby-blue/20 shadow-sm">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          {/* Top bar (brand + nav + cta) */}
          <div className="flex items-center justify-between py-3">
            {/* Brand */}
            <Link to="/" className="flex items-center space-x-4 group">
              <div className="w-16 h-16 rounded-full overflow-hidden shadow-md bg-white">
                <img
                  src={LOGO_MARK}
                  alt="Logo R√©sidence St-Antoine"
                  className="w-full h-full object-contain transition-transform duration-300 group-hover:scale-105"
                />
              </div>
              <div className="h-14">
                <img
                  src={LOGO_WORD}
                  alt="R√©sidence St-Antoine"
                  className="h-full w-auto object-contain"
                />
              </div>
            </Link>

            {/* Desktop nav */}
            <nav className="hidden md:flex items-center space-x-2 lg:space-x-4">
              {navigation.map((item) => (
                <Link
                  key={item.name}
                  to={item.href}
                  className={`px-4 py-2 rounded-lg text-base lg:text-lg transition-all duration-200 outline-none
                    focus-visible:ring-2 focus-visible:ring-granby-blue-light focus-visible:ring-offset-2
                    ${isActive(item.href)
                      ? 'bg-granby-bg-blue text-granby-blue-dark font-semibold'
                      : 'text-gray-700 hover:text-granby-blue-dark hover:bg-granby-bg-blue'
                    }`}
                >
                  <span className="font-medium">{item.name}</span>
                </Link>
              ))}
            </nav>

            {/* Desktop CTA */}
            <div className="hidden md:block">
              <Link
                to="/admission"
                className="px-5 py-2.5 rounded-lg bg-granby-blue-dark text-white font-medium shadow-granby
                           hover:shadow-granby-lg transition-all duration-200 outline-none
                           focus-visible:ring-2 focus-visible:ring-granby-blue-light focus-visible:ring-offset-2"
              >
                Planifier une visite
              </Link>
            </div>

            {/* Mobile menu button */}
            <button
              onClick={() => setIsMenuOpen((v) => !v)}
              className="md:hidden p-2 rounded-lg text-gray-700 hover:bg-granby-bg-blue outline-none
                         focus-visible:ring-2 focus-visible:ring-granby-blue-light focus-visible:ring-offset-2"
              aria-label={isMenuOpen ? 'Fermer le menu' : 'Ouvrir le menu'}
            >
              {isMenuOpen ? <X size={22} /> : <Menu size={22} />}
            </button>
          </div>

          {/* Mobile nav */}
          <AnimatePresence>
            {isMenuOpen && (
              <motion.div
                initial={{ opacity: 0, height: 0 }}
                animate={{ opacity: 1, height: 'auto' }}
                exit={{ opacity: 0, height: 0 }}
                className="md:hidden border-t border-granby-blue/20"
              >
                <div className="py-3 space-y-2">
                  {navigation.map((item) => (
                    <Link
                      key={item.name}
                      to={item.href}
                      onClick={() => setIsMenuOpen(false)}
                      className={`block mx-2 px-4 py-3 rounded-lg text-base transition-colors duration-200 outline-none
                        focus-visible:ring-2 focus-visible:ring-granby-blue-light
                        ${isActive(item.href)
                          ? 'bg-gradient-granby text-white'
                          : 'text-gray-700 hover:bg-granby-bg-blue'
                        }`}
                    >
                      {item.name}
                    </Link>
                  ))}
                  <div className="px-2">
                    <Link
                      to="/admission"
                      onClick={() => setIsMenuOpen(false)}
                      className="block text-center px-4 py-3 rounded-lg bg-granby-blue-dark text-white font-medium shadow-granby
                                 hover:shadow-granby-lg transition-all duration-200 outline-none
                                 focus-visible:ring-2 focus-visible:ring-granby-blue-light"
                    >
                      Planifier une visite
                    </Link>
                  </div>
                </div>
              </motion.div>
            )}
          </AnimatePresence>
        </div>
      </header>

      {/* ---------- MAIN ---------- */}
      <main>{children}</main>

      {/* ---------- FOOTER ---------- */}
      <footer className="mt-20 bg-granby-bg-blue text-gray-900">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-10">
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-8">
            {/* Logo + description */}
            <div className="lg:col-span-2">
              <div className="flex items-center space-x-4 mb-5">
                <div className="w-16 h-16 rounded-full overflow-hidden shadow-md bg-white">
