import React from "react";
import { Link, useLocation } from "react-router-dom";
import { motion, AnimatePresence } from "framer-motion";

const Layout = ({ children }) => {
  const location = useLocation();
  const navItems = [
    { name: "Accueil", path: "/" },
    { name: "Qui sommes nous", path: "/about" },
    { name: "Contact", path: "/contact" },
    { name: "Candidature", path: "/application" },
    { name: "Carrières", path: "/careers" }
  ];

  return (
    <div className="min-h-screen bg-white">
      <header className="bg-white shadow sticky top-0 z-50">
        <div className="max-w-7xl mx-auto flex justify-between items-center px-4 py-4">
          <div className="flex items-center space-x-3">
            <div className="bg-gradient-granby w-12 h-12 rounded-xl flex justify-center items-center">
              <span className="text-white text-xl font-bold">RG</span>
            </div>
            <div>
              <h1 className="text-xl font-bold text-gray-800">Résidence Granby</h1>
              <p className="text-sm text-granby-blue">Soins spécialisés • Troubles neurocognitifs</p>
            </div>
          </div>
          <nav className="hidden md:flex space-x-6">
            {navItems.map((item) => (
              <Link key={item.name} to={item.path} className="text-gray-700 hover:text-granby-blue">
                {item.name}
              </Link>
            ))}
          </nav>
        </div>
      </header>
      <main>{children}</main>
    </div>
  );
};

export default Layout;
