import React, { useState } from 'react';
import { motion } from 'framer-motion';
import {
    Phone, Mail, MapPin, Clock, Calendar, Send, CheckCircle
} from 'lucide-react';

const Admission = () => {
    const [formData, setFormData] = useState({
        name: '',
        email: '',
        phone: '',
        visitDate: '',
        message: '',
    });
    const [isSubmitted, setIsSubmitted] = useState(false);

    const handleSubmit = (e) => {
        e.preventDefault();
        setIsSubmitted(true);
        setTimeout(() => setIsSubmitted(false), 4000);
    };

    const handleChange = (e) => {
        setFormData({
            ...formData,
            [e.target.name]: e.target.value
        });
    };

    const contactInfo = [
        {
            icon: Phone,
            title: "Téléphone",
            details: "(450) 555-0123",
            description: "Disponible 24h/7 pour urgences",
        },
        {
            icon: Mail,
            title: "Email",
            details: "info@residencegranby.ca",
            description: "Réponse sous 24h",
        },
        {
            icon: MapPin,
            title: "Adresse",
            details: "123 Rue Principale, Granby, QC J2G 1A1",
            description: "Facile d'accès, stationnement gratuit",
        },
        {
            icon: Clock,
            title: "Heures de visite",
            details: "Lundi au dimanche: 9h à 18h",
            description: "Visites sur rendez-vous préférées",
        }
    ];

    return (
        <div className="font-merriweather">
            {/* Hero */}
            <section className="relative min-h-[60vh] flex items-center justify-center">
                <div
                    className="absolute inset-0 bg-cover bg-center bg-no-repeat"
                    style={{
                        backgroundImage: `linear-gradient(135deg, rgba(166, 204, 230, 0.9), rgba(186, 174, 217, 0.9)), url('https://images.pexels.com/photos/8728559/pexels-photo-8728559.jpeg')`
                    }}
                />
                <div className="relative z-10 text-white text-center space-y-6 px-4">
                    <motion.h1
                        initial={{ opacity: 0, y: 30 }}
                        animate={{ opacity: 1, y: 0 }}
                        transition={{ duration: 0.8 }}
                        className="text-5xl font-playfair font-bold"
                    >
                        Contact et Admission
                    </motion.h1>
                    <motion.p
                        initial={{ opacity: 0, y: 30 }}
                        animate={{ opacity: 1, y: 0 }}
                        transition={{ delay: 0.3, duration: 0.8 }}
                        className="text-xl text-white/90 max-w-3xl mx-auto"
                    >
                        Vous souhaitez faire une demande d'admission ? Commencez ici et notre équipe vous contactera rapidement.
                    </motion.p>
                </div>
            </section>

            {/* Infos contact */}
            <section className="py-20 bg-white">
                <div className="max-w-7xl mx-auto px-4 grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-8">
                    {contactInfo.map(({ icon: Icon, title, details, description }, index) => (
                        <motion.div
                            key={title}
                            initial={{ opacity: 0, y: 30 }}
                            whileInView={{ opacity: 1, y: 0 }}
                            transition={{ duration: 0.6, delay: index * 0.1 }}
                            className="text-center"
                        >
                            <div className="w-16 h-16 bg-[#A6CCE6]/20 rounded-xl flex items-center justify-center mx-auto mb-4">
                                <Icon size={32} className="text-[#4D4D4D]" />
                            </div>
                            <h3 className="text-xl font-bold text-gray-900 mb-2">{title}</h3>
                            <p className="text-gray-700 font-medium mb-2">{details}</p>
                            <p className="text-gray-500 text-sm">{description}</p>
                        </motion.div>
                    ))}
                </div>
            </section>

            {/* Formulaire */}
            <section className="py-20 bg-gradient-to-b from-[#A6CCE6]/20 to-white">
                <div className="max-w-4xl mx-auto bg-white rounded-2xl shadow-granby-lg p-10">
                    <motion.h2
                        initial={{ opacity: 0, y: 30 }}
                        whileInView={{ opacity: 1, y: 0 }}
                        transition={{ duration: 0.8 }}
                        className="text-4xl font-playfair font-bold text-center text-gray-900 mb-8"
                    >
                        Demande d’admission pour un proche
                    </motion.h2>

                    {isSubmitted ? (
                        <div className="text-center py-10">
                            <CheckCircle className="text-green-500 mx-auto mb-4" size={48} />
                            <h3 className="text-xl font-bold text-gray-900 mb-2">Soumis avec succès !</h3>
                            <p className="text-gray-600">Nous vous contacterons dans les 48 heures.</p>
                        </div>
                    ) : (
                        <form onSubmit={handleSubmit} className="space-y-6">
                            <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                                <div>
                                    <label className="block text-sm font-semibold text-gray-700 mb-2">Nom complet *</label>
                                    <input
                                        type="text"
                                        name="name"
                                        required
                                        value={formData.name}
                                        onChange={handleChange}
                                        className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-granby-blue"
                                        placeholder="Votre nom"
                                    />
                                </div>
                                <div>
                                    <label className="block text-sm font-semibold text-gray-700 mb-2">Téléphone *</label>
                                    <input
                                        type="tel"
                                        name="phone"
                                        required
                                        value={formData.phone}
                                        onChange={handleChange}
                                        className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-granby-blue"
                                        placeholder="(450) 555-0123"
                                    />
                                </div>
                            </div>

                            <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                                <div>
                                    <label className="block text-sm font-semibold text-gray-700 mb-2">Email *</label>
                                    <input
                                        type="email"
                                        name="email"
                                        required
                                        value={formData.email}
                                        onChange={handleChange}
                                        className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-granby-blue"
                                        placeholder="votre@email.com"
                                    />
                                </div>
                                <div>
                                    <label className="block text-sm font-semibold text-gray-700 mb-2">Date souhaitée pour une visite *</label>
                                    <input
                                        type="date"
                                        name="visitDate"
                                        required
                                        value={formData.visitDate}
                                        onChange={handleChange}
                                        className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-granby-blue"
                                    />
                                </div>
                            </div>

                            <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                                <div>
                                    <label className="block text-sm font-semibold text-gray-700 mb-2">Nom du résident potentiel *</label>
                                    <input
                                        type="text"
                                        name="residentName"
                                        required
                                        value={formData.residentName || ''}
                                        onChange={handleChange}
                                        className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-granby-blue"
                                        placeholder="Nom du résident"
                                    />
                                </div>
                                <div>
                                    <label className="block text-sm font-semibold text-gray-700 mb-2">Âge du résident *</label>
                                    <input
                                        type="number"
                                        name="residentAge"
                                        required
                                        value={formData.residentAge || ''}
                                        onChange={handleChange}
                                        className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-granby-blue"
                                        placeholder="Âge"
                                    />
                                </div>
                            </div>

                            <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                                <div>
                                    <label className="block text-sm font-semibold text-gray-700 mb-2">Relation avec le résident *</label>
                                    <input
                                        type="text"
                                        name="relation"
                                        required
                                        value={formData.relation || ''}
                                        onChange={handleChange}
                                        className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-granby-blue"
                                        placeholder="Fils, fille, proche aidant, etc."
                                    />
                                </div>
                                <div>
                                    <label className="block text-sm font-semibold text-gray-700 mb-2">Type de soins recherchés *</label>
                                    <select
                                        name="careType"
                                        required
                                        value={formData.careType || ''}
                                        onChange={handleChange}
                                        className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-granby-blue"
                                    >
                                        <option value="">Choisir...</option>
                                        <option value="autonome">Résidence pour personnes autonomes</option>
                                        <option value="semi-autonome">Semi-autonome</option>
                                        <option value="troubles-neurocognitifs">Troubles neurocognitifs / Alzheimer</option>
                                    </select>
                                </div>
                            </div>

                            <div>
                                <label className="block text-sm font-semibold text-gray-700 mb-2">Message</label>
                                <textarea
                                    name="message"
                                    rows={4}
                                    value={formData.message}
                                    onChange={handleChange}
                                    className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-granby-blue resize-none"
                                    placeholder="Précisez vos besoins, vos attentes, ou toute autre information pertinente."
                                />
                            </div>

                            <button
                                type="submit"
                                className="w-full bg-gradient-to-r from-[#A6CCE6] to-[#BAAED9] text-white font-semibold py-4 px-6 rounded-lg hover:shadow-granby-lg transition-all duration-300 flex items-center justify-center"
                            >
                                <Send className="mr-2" size={20} /> Envoyer la demande
                            </button>
                        </form>
                    )}
                </div>
            </section>
        </div>
    );
};

export default Admission;
