import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import { motion } from 'framer-motion';
import {
  Shield,
  Heart,
  Users,
  Star,
  Phone,
  Calendar,
  ArrowRight,
  CheckCircle,
  Award,
  ChevronLeft,
  ChevronRight
} from 'lucide-react';

const Home = () => {
  const services = [
    {
      title: "Sécurité et Tranquillité",
      description: "Surveillance 24h/24 avec système de sécurité avancé et équipe de professionnels formés.",
      icon: Shield,
      color: "granby-blue"
    },
    {
      title: "Engagement et Expertise",
      description: "Spécialisés en troubles neurocognitifs avec accompagnement jusqu'à la fin de vie.",
      icon: Award,
      color: "granby-violet"
    },
    {
      title: "Respect et Dignité",
      description: "Accompagnement bienveillant respectant l'individualité et l'histoire de chaque résident.",
      icon: Heart,
      color: "granby-blue"
    },
    {
      title: "Soins Personnalisés",
      description: "Adaptation de nos soins selon les besoins spécifiques et préférences de chacun.",
      icon: Users,
      color: "granby-violet"
    },
    {
      title: "Environnement Familial",
      description: "Ambiance réconfortante favorisant un sentiment d'appartenance et de sécurité.",
      icon: Heart,
      color: "granby-blue"
    }
  ];

  const includedServices = [
    "3 repas et 2 collations par jour",
    "Ménage et buanderie hebdomadaires",
    "Électricité, chauffage, eau chaude et câble",
    "Loisirs et activités adaptées",
    "Surveillance 24h/7",
    "Infirmier(ère) auxiliaires et préposés 7j/7"
  ];

  const temoignages = [
    {
      texte: "La Résidence St-Antoine a complètement changé la vie de ma mère. Elle s'y sent en sécurité, entourée et respectée. Merci à toute l'équipe pour votre dévouement!",
      auteur: "Claire B., fille d'une résidente",
      source: "Google Avis, 2024"
    },
    {
      texte: "Nous avons visité plusieurs établissements, mais rien ne se compare à la douceur et à l'attention reçues ici. Mon père s'est réconcilié avec la vie en communauté.",
      auteur: "Youssef M., fils d’un résident",
      source: "Facebook, 2024"
    },
    {
      texte: "Je suis soulagée de voir ma tante entre de bonnes mains. L'équipe est bienveillante et professionnelle. Je recommande chaleureusement la Résidence St-Antoine.",
      auteur: "Fatima Z., nièce d'une résidente",
      source: "Témoignage privé, 2024"
    }
  ];

  const [index, setIndex] = useState(0);
  const suivant = () => setIndex((index + 1) % temoignages.length);
  const precedent = () => setIndex((index - 1 + temoignages.length) % temoignages.length);

  return (
    <div className="min-h-screen font-sans">
      {/* ... toutes les sections précédentes restent inchangées ... */}

      {/* Services Inclus Section */}
      <section className="py-20">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="grid grid-cols-1 lg:grid-cols-2 gap-16 items-center">
            <motion.div
              initial={{ opacity: 0, x: -30 }}
              whileInView={{ opacity: 1, x: 0 }}
              transition={{ duration: 0.8 }}
            >
              <h2 className="text-4xl lg:text-5xl font-playfair text-gray-900 mb-8">
                Services Inclus
              </h2>
              <p className="text-xl text-gray-600 font-sans mb-8">
                Tout ce dont vos proches ont besoin pour une vie confortable et sécuritaire.
              </p>
              <div className="space-y-4">
                {includedServices.map((service, index) => (
                  <motion.div
                    key={index}
                    initial={{ opacity: 0, x: -20 }}
                    whileInView={{ opacity: 1, x: 0 }}
                    transition={{ duration: 0.5, delay: index * 0.1 }}
                    className="flex items-center space-x-3"
                  >
                    <CheckCircle className="text-granby-blue flex-shrink-0" size={24} />
                    <span className="text-gray-700 text-lg font-sans">{service}</span>
                  </motion.div>
                ))}
              </div>
            </motion.div>
            <motion.div
              initial={{ opacity: 0, x: 30 }}
              whileInView={{ opacity: 1, x: 0 }}
              transition={{ duration: 0.8 }}
              className="relative"
            >
              <img
                src="https://images.unsplash.com/photo-1536064479547-7ee40b74b807"
                alt="Professionnel de santé"
                className="rounded-2xl shadow-granby-lg w-full h-96 object-cover"
              />
            </motion.div>
          </div>
        </div>
      </section>

      {/* Témoignages Section */}
      <section className="py-20 bg-gray-50">
        <div className="max-w-4xl mx-auto px-4 text-center">
          <h2 className="text-4xl lg:text-5xl font-playfair text-gray-900 mb-12">
            Ce que disent les familles
          </h2>

          <div className="relative bg-white p-8 rounded-xl shadow-granby text-left">
            <div className="text-6xl text-granby-blue mb-4">“</div>
            <p className="text-xl font-merriweather text-gray-700 leading-relaxed mb-6">
              {temoignages[index].texte}
            </p>
            <div className="border-l-4 border-granby-violet pl-4">
              <p className="uppercase font-bold text-sm text-gray-800 font-sans">
                {temoignages[index].auteur}
              </p>
              <p className="text-gray-500 text-sm font-sans">
                {temoignages[index].source}
              </p>
            </div>
            <div className="absolute top-1/2 -translate-y-1/2 left-4">
              {index > 0 && (
                <button onClick={precedent} className="bg-granby-blue/20 p-2 rounded-full hover:bg-granby-blue">
                  <ChevronLeft className="text-granby-blue" />
                </button>
              )}
            </div>
            <div className="absolute top-1/2 -translate-y-1/2 right-4">
              {index < temoignages.length - 1 && (
                <button onClick={suivant} className="bg-granby-blue/20 p-2 rounded-full hover:bg-granby-blue">
                  <ChevronRight className="text-granby-blue" />
                </button>
              )}
            </div>
          </div>

          <div className="flex justify-center mt-6 space-x-2">
            {temoignages.map((_, i) => (
              <div
                key={i}
                className={`w-3 h-3 rounded-full ${i === index ? 'bg-granby-violet' : 'bg-gray-300'} transition-colors`}
              ></div>
            ))}
          </div>
        </div>
      </section>
    </div>
  );
};

export default Home;
